	public static List<Object> parseArgs(final String args) throws ParseException {
		if (isEmpty(args))
			return Collections.emptyList();
		boolean inEscape = false;
		boolean inQuote = false;
		List<Object> result = new ArrayList<Object>();
		StringBuilder arg = new StringBuilder();
		for (int i = 0; i < args.length(); i++) {
			char c = args.charAt(i);
			switch (c) {
								case ' ':
				case '\n':
				case '\t':
					if (inQuote) {
						arg.append(c);
						break;
					}
					if (arg.length() > 0) {
						result.add(arg.toString()); 						arg = new StringBuilder(); 					}
					break;
								case '\\':
					if (inQuote) {
						if (inEscape) {
							arg.append('\\');
							inEscape = false;
						}
						else {
							inEscape = true;
						}
					}
					break;
								case '"':
					if (inQuote) {
						if (inEscape) {
							arg.append('"');
							inEscape = false;
						}
						else {
							result.add(arg.toString()); 							arg = new StringBuilder(); 							inQuote = false;
						}
					}
					else {
						inQuote = true;
					}
					break;
				default:
					arg.append(c);
			}
						if (i == args.length() - 1) {
				result.add(arg.toString()); 							}
		}
		return result;
	}
	public static int range(final int value, final int failSafeValue, final int minValue, final int maxValue) {
		if (minValue > maxValue)
			throw new IllegalArgumentException(String.format("minValue (%d) > maxValue (%d)", minValue, maxValue));
		return ((value < minValue) || (value > maxValue)) ? failSafeValue : value;
	}
	public static String rightSqueeze(final String text, final int maxLength) {
		if (text == null)
			return null;
		if (text.length() > maxLength)
			return text.substring(0, maxLength - 1) + "...";
		return text;
	}
