			return "";
		boolean addComma = false;
		StringBuilder buf = new StringBuilder(1024);
		for (Object i : values) {
			if (addComma)
				buf.append(", ");
			buf.append(i.toString());
			addComma = true;
		}
		return buf.toString();
	}
