	public static String unescapeXML(String value) {
		if (TK.isEmpty(value))
			return value;
		value = value.replace("&#039;", "\'");
		value = value.replace("&apos;", "\'");
		value = value.replace("&quot;", "\"");
		value = value.replace("&lt;", "<");
		value = value.replace("&gt;", ">");
		value = value.replace("&amp;", "&");
		return value;
	}
