"package org.makagiga.commons;
import static java.awt.Color.*;
import java.awt.event.ActionEvent;
import java.awt.event.ActionListener;
import java.awt.event.MouseEvent;
import javax.swing.Action;
import javax.swing.Icon;
import javax.swing.JButton;
public class MButton extends JButton implements Signal {
		public static final String CLICKED = "clicked"; 
	public MButton() {
		this(null, (Icon)null);
	}
	public MButton(final Action action) {
		super(action);
		init();
	}
	public MButton(final Icon icon) {
		this(null, icon);
	}
	public MButton(final String text) {
		this(text, (Icon)null);
	}
	public MButton(final String text, final Icon icon) {
		super(text, icon);
		init();
	}
	public MButton(final String text, final String iconName) {
		this(text, FS.getIcon(iconName));
	}
	public static void installSpecialEffects(final JButton button) {
		if (!UI.isMetal())
			return;
		button.addMouseListener(new MMouseAdapter() {
			@Override
			public void mouseClicked(final MouseEvent e) {
				if (!button.isEnabled())
					return;
				Blend.background(button, WHITE, UI.getColor("MenuItem.selectionBackground"));
			}
			@Override
			public void mouseEntered(final MouseEvent e) {
				if (button.isEnabled()) {
					button.setBorderPainted(true);
					button.setContentAreaFilled(true);
					Blend.background(button, WHITE, UI.getColor("MenuItem.selectionBackground"));
				}
			}
			@Override
			public void mouseExited(final MouseEvent e) {
				button.setBorderPainted(false);
				button.setContentAreaFilled(false);
			}
		} );
		button.setBorderPainted(false);
		button.setContentAreaFilled(false);
	}
	public void setIcon(final String value) {
		setIcon(FS.getIcon(value));
	}
	public void connect(final String signal, final Object receiver, final String slot, final Object... args) {
		MObject.connect(this, signal, receiver, slot);
	}
	public void emit(final String signal, final Object... args) {
		MObject.emit(this, signal, args);
	}
	protected void onClick() { }
	private void init() {
		addActionListener(new ActionListener() {
			public final void actionPerformed(final ActionEvent e) {
				MButton.this.onClick();
				MButton.this.emit("clicked");
			}
		} );
	}
}"

