"package org.makagiga.commons;
import static org.makagiga.commons.UI._;
import static java.awt.event.KeyEvent.*;
import javax.swing.Icon;
public final class MActionInfo {
	public static final MActionInfo ABOUT = new MActionInfo(_("About"), "ui/info");
	public static final MActionInfo CANCEL = new MActionInfo(_("Cancel"), "ui/cancel");
	public static final MActionInfo CLEAR = new MActionInfo(_("Clear"), "ui/clearleft");
	public static final MActionInfo CLOSE = new MActionInfo(_("Close"), "ui/close", VK_W, CTRL_MASK);
	public static final MActionInfo COPY = new MActionInfo(_("Copy"), "ui/copy", VK_C, CTRL_MASK);
	public static final MActionInfo CUT = new MActionInfo(_("Cut"), "ui/cut", VK_X, CTRL_MASK);
	public static final MActionInfo DELETE = new MActionInfo(_("Delete"), "ui/delete", VK_DELETE, 0);
	public static final MActionInfo DELETE_NO_KEY_STROKE = new MActionInfo(_("Delete"), "ui/delete");
	public static final MActionInfo INSTALL = new MActionInfo(_("Install"), "ui/save");
	public static final MActionInfo NEW = new MActionInfo(_("New"), "ui/newfile", VK_N, CTRL_MASK);
	public static final MActionInfo OK = new MActionInfo(_("OK"), "ui/ok");
	public static final MActionInfo PASTE = new MActionInfo(_("Paste"), "ui/paste", VK_V, CTRL_MASK);
	public static final MActionInfo PRINT = new MActionInfo(_("Print..."), "ui/print", VK_P, CTRL_MASK);
	public static final MActionInfo QUIT = new MActionInfo(_("Quit"), "ui/quit", VK_Q, CTRL_MASK + SHIFT_MASK);
	public static final MActionInfo REVERT = new MActionInfo(_("Revert"), "ui/revert");
	public static final MActionInfo SAVE = new MActionInfo(_("Save"), "ui/save", VK_S, CTRL_MASK);
	public static final MActionInfo SET_AS_DEFAULT = new MActionInfo(_("Set As Default"), "ui/save");
	public static final MActionInfo SET_DEFAULT_VALUES = new MActionInfo(_("Set Default Values"), "ui/revert");
	public static final MActionInfo SETTINGS = new MActionInfo(_("Settings..."), "ui/configure");
	public static final MActionInfo STARRED = new MActionInfo(_("Starred"), "ui/star");
	public static final MActionInfo UNINSTALL = new MActionInfo(_("Uninstall"), "ui/delete");
	private int keyCode;
	private int modifiers;
	private String iconName;
	private String text;
	public MActionInfo(final String text, final String iconName) {
		this(text, iconName, 0, 0);
	}
	public MActionInfo(final String text, final String iconName, final int keyCode, final int modifiers) {
		this.text = text;
		this.iconName = iconName;
		this.keyCode = keyCode;
		this.modifiers = modifiers;
	}
	public Icon getIcon() {
		return MIcon.stock(iconName);
	}
	public String getIconName() { return iconName; }
	public int getKeyCode() { return keyCode; }
	public int getModifiers() { return modifiers; }
	public Icon getSmallIcon() {
		return MIcon.small(iconName);
	}
	public String getText() { return text; }
}"

