	public void sync() {
		if (config == null)
			return;
		if (!isWriteable())
			return;
		config.write("bookmark", (attr & BOOKMARK) != 0);
		if (TK.isEmpty(categories))
			config.removeString("categories");
		else
			config.write("categories", categories);
		config.write("color", color);
		config.write("comment", comment);
		config.writeDate("created", createdTime);
		if (isFile())
			config.write("encrypted", (attr & ENCRYPTED) != 0);
		config.write("icon", iconName);
		config.write("id", id);
		config.writeDate("lastOpen", openTime);
		config.write("locked", (attr & LOCKED) != 0);
				config.write("rating", rating);
		if (TK.isEmpty(tags))
			config.removeString("tags");
		else
			config.write("tags", tags);
		config.write("usage", usage);
		config.write("version", VERSION);
				if (isFolder()) {
			config.write("mount", getMount());
		}
		config.sync();
	}
	private static String formatDate(final long date) {
		if (date == 0)
			return null;
		return new MDate(date).fancyFormat(MDate.LONG, true);
	}
	private String getPathWithoutExtension(final String pathWithExtension) {
