				return false;
			default: 				MActionInfo other = (MActionInfo)o;
				return
					(this.keyCode == other.keyCode) &&
					(this.modifiers == other.modifiers) &&
					MObject.safeEquals(this.iconName, other.iconName) &&
					MObject.safeEquals(this.text, other.text);
		}
	}
