			laf = "javax.swing.plaf.metal.MetalLookAndFeel";
				if ("javax.swing.plaf.metal.MetalLookAndFeel".equals(laf))
			laf = "javax.swing.plaf.nimbus.NimbusLookAndFeel";
		return laf;
	}
