	public static <T> T invokeAndWait(final Callable<T> c) throws Exception {
		if (EventQueue.isDispatchThread()) {
			return c.call();
		}
		else {
			final Property<Exception> exception = new Property<>();
			final Property<T> result = new Property<>();
			EventQueue.invokeAndWait(new Runnable() {
				public void run() {
					try {
						result.set(c.call());
					}
					catch (Exception e) {
						exception.set(e);
					}
				}
			} );
			if (!exception.isNull())
				throw exception.get();
			return result.get();
		}
	}
	public static void invokeAndWait(final Runnable r) throws InterruptedException, InvocationTargetException {
		if (EventQueue.isDispatchThread())
			r.run();
		else
			EventQueue.invokeAndWait(r);
	}
