	public void animatedResize(final Dimension size) {
		if (!isAnimationEnabled() || !UI.animations.get()) {
			setSize(size);
			return;
		}
		resizeAnimation = TK.dispose(resizeAnimation);
		resizeAnimation = new MTimeline<>(this, 100);
		resizeAnimation.addPropertyToInterpolate(
			MTimeline.<Dimension>property("size")
			.from(getSize())
			.to(size)
			.getWith(new TimelinePropertyBuilder.PropertyGetter<Dimension>() {
				@Override
				public Dimension get(final Object o, final String name) {
					return getSize();
				}
			} )
			.setWith(new TimelinePropertyBuilder.PropertySetter<Dimension>() {
				@Override
				public void set(final Object o, final String name, final Dimension value) {
					setSize(value);
				}
			} )
		);
		resizeAnimation.play();
	}
