	protected boolean done(final boolean value) {
		code = value;
				if (code) {
			if (!onAccept())
				return false; 		}
				else {
			if ((cancelButton != null) && !cancelButton.isEnabled())
				return false;
			if (!onReject())
				return false; 		}
		onClose();
		closed = true;
		Mods.exec(this, MOD_CLOSE);
		dispose();
				if (flags.isSet(CACHED))
			return true;
		resizeAnimation = TK.dispose(resizeAnimation);
		validatorSupport.removeAll();
		uninstallComponents();
		return true;
	}
	protected Mnemonic getMnemonic() { return mnemonic; }
	protected boolean onAccept() { return true; }
	protected void onClose() { }
	protected void onDetailsClick(final boolean visible) { }
	protected void onHelpClick() {
		if (Help.show(this, helpContext))
			return;
		if (whatsThis == null) {
			List<MWhatsThis.Info> all = MWhatsThis.getAll(this);
			if (all.isEmpty())
				return; 		
			whatsThis = new MWhatsThis(this);
			setGlassPane(whatsThis);
			whatsThis.setVisible(true);
		}
		else {
			MFrame.clearGlassPane(this);
			whatsThis = null;
		}
	}
	protected void onLinkClick() {
		if (linkButton != null)
			linkButton.doOpen();
	}
	protected boolean onReject() { return true; }
	protected void onUserClick() { }
	protected void setDetailsVisible(final Component c, final boolean visible) {
				if (resizeAnimation != null)
			resizeAnimation.end();
		c.setVisible(visible);
		int newHeight;
		if (visible)
			newHeight = getHeight() + c.getPreferredSize().height;
		else
			newHeight = getHeight() - c.getHeight();
		if (isAnimationEnabled() && UI.animations.get()) {
			TK.dispose(resizeAnimation);
			resizeAnimation = new MTimeline<>(this, 100);
			resizeAnimation.addPropertyToInterpolate("height", getHeight(), newHeight);
			resizeAnimation.play();
		}
		else {
			setHeight(newHeight);
		}
	}
	private static Window checkOwner(final Window owner) {
		return
			((owner instanceof Dialog) || (owner instanceof Frame))
			? owner
			: null;
	}
	private TriBoolean disableButton(final AbstractButton button) {
		TriBoolean state =
			(button == null)
			? TriBoolean.UNDEFINED
			: TriBoolean.of(button.isEnabled());
		if (state.isTrue())
			button.setEnabled(false);
		return state;
	}
	private void doCancel() {
		if ((cancelButton != null) && cancelButton.isEnabled() && cancelButton.isVisible())
			cancelButton.doClick();
		else
			reject();
	}
	private boolean doClick(final AbstractButton b) {
		if ((b != null) && b.hasFocus() && b.isEnabled() && b.isVisible()) {
			b.doClick();
			return true;
		}
		return false;
	}
	private void enableButton(final AbstractButton button, final TriBoolean state) {
		if (state.isTrue())
			button.setEnabled(true);
	}
	private void init(final String title, final Icon icon) {
		setDefaultCloseOperation(DO_NOTHING_ON_CLOSE);
		setModal(flags.isSet(MODAL));
		Dimension defaultSize = new Dimension(64, 64);
		setMinimumSize(defaultSize);
		setSize(defaultSize);
		setTitle(title);
				contentPane = MPanel.createBorderPanel();
		setBackground(contentPane.getBackground());
		setContentPane(contentPane);
		if (flags.isSet(OK_BUTTON) || flags.isSet(CANCEL_BUTTON) || flags.isSet(APPLY_BUTTON)) {
			buttonsPanel =
				flags.isSet(SIDE_BUTTONS)
				? MPanel.createVBoxPanel()
				: MPanel.createHBoxPanel();
			buttonsPanel.setOpaque(false);
						if (flags.isSet(HELP_BUTTON)) {
				helpButton = new MButton() {
					@Override
					protected void onClick() {
						MDialog dialog = (MDialog)this.getWindowAncestor();
						dialog.onHelpClick();
					}
				};
				if (UI.isMac()) {
					helpButton.setText("?");
					helpButton.putClientProperty("JButton.buttonType", "help");
				}
				else if (UI.isQuaqua()) {
					helpButton.setText("?");
					helpButton.putClientProperty("Quaqua.Button.style", "help");
				}
				else {
					if (UI.buttonIcons.get())
						helpButton.setIconName(MActionInfo.HELP.getIconName());
					else
						helpButton.setText("?");
				}
				helpButton.setToolTipText(MActionInfo.HELP.getText());
				buttonsPanel.add(helpButton);
			}
						if (flags.isSet(DETAILS_BUTTON)) {
				detailsButton = new MToggleButton(_("Details")) {
					@Override
					protected void onClick() {
						MDialog dialog = (MDialog)this.getWindowAncestor();
						dialog.onDetailsClick(this.isSelected());
					}
				};
				detailsButton.setIcon(ShapeIcon.RIGHT_ARROW);
				detailsButton.setRolloverIcon(ShapeIcon.RIGHT_ARROW);
				detailsButton.setSelectedIcon(ShapeIcon.DOWN_ARROW);
				if (flags.isSet(HELP_BUTTON))
					buttonsPanel.addContentGap();
				buttonsPanel.add(detailsButton);
			}
						if (flags.isSet(USER_BUTTON)) {
				userButton = new MButton() {
					@Override
					protected void onClick() {
						MDialog dialog = (MDialog)this.getWindowAncestor();
						dialog.onUserClick();
					}
				};
				if (flags.isSet(DETAILS_BUTTON) || flags.isSet(HELP_BUTTON))
					buttonsPanel.addContentGap();
				buttonsPanel.add(userButton);
			}
						if (flags.isSet(LINK_BUTTON)) {
				linkButton = new MLinkButton() {
					@Override
					protected void onClick() {
						MDialog dialog = (MDialog)this.getWindowAncestor();
						dialog.onLinkClick();
					}
				};
				if (flags.isSet(HELP_BUTTON) || flags.isSet(USER_BUTTON))
					buttonsPanel.addContentGap();
				buttonsPanel.add(linkButton);
			}
						if (flags.isSet(OK_BUTTON)) {
				okButton = new MButton() {
					@Override
					protected void onClick() {
						MDialog dialog = (MDialog)this.getWindowAncestor();
						if (dialog != null)
							dialog.accept();
					}
				};
				okButton.setMinimumWidth(75);
				if (flags.isSet(CLOSE_BUTTON)) {
					okButton.setIconNameUI("ui/close");
					okButton.setText(mnemonic.apply(okButton, _("&Close")));
				}
				else {
					okButton.setIconNameUI("ui/ok");
					okButton.setText(mnemonic.apply(okButton, _("&OK")));
				}
			}
			if (flags.isSet(CANCEL_BUTTON)) {
				if (UI.isRetro()) {
					cancelButton = new MButton() {
						@Override
						protected void onClick() {
							MDialog dialog = (MDialog)this.getWindowAncestor();
							if (dialog != null)
								dialog.reject();
						}
					};
					cancelButton.setIconNameUI("ui/cancel");
				}
				else {
					cancelButton = new MLinkButton() {
						@Override
						protected void onClick() {
							MDialog dialog = (MDialog)this.getWindowAncestor();
							if (dialog != null)
								dialog.reject();
						}
					};
					cancelButton.setHorizontalAlignment(MLinkButton.CENTER);
				}
				cancelButton.setText(mnemonic.apply(cancelButton, _("&Cancel")));
			}
			if (flags.isSet(APPLY_BUTTON)) {
				applyButton = new MButton() {
					@Override
					protected void onClick() {
						MDialog dialog = (MDialog)this.getWindowAncestor();
						dialog.onAccept();
					}
				};
				applyButton.setIconNameUI("ui/apply");
				applyButton.setText(mnemonic.apply(applyButton, _("&Apply")));
			}
						int okCancelPanelFlags;
			if (flags.isSet(SIDE_BUTTONS))
				okCancelPanelFlags = MButtonPanel.VERTICAL;
			else
				okCancelPanelFlags = MButtonPanel.HORIZONTAL;
			okCancelPanel = new MButtonPanel(
				okCancelPanelFlags | MButtonPanel.NO_PAINTER,
				okButton,
				applyButton,
				cancelButton
			);
			if (cancelButton instanceof MLinkButton) {
				Color linkColor = UI.getForeground(okCancelPanel);
				MLinkButton urlButton = (MLinkButton)cancelButton;
				urlButton.setLinkColor(linkColor);
				urlButton.setHoverLinkColor(linkColor);
			}
			MPanel bottomPanel = MPanel.createBorderPanel(MPanel.DEFAULT_CONTENT_MARGIN);
			if (flags.isSet(SIDE_BUTTONS))
				bottomPanel.setMargin(MPanel.DEFAULT_CONTENT_MARGIN, 0, MPanel.DEFAULT_CONTENT_MARGIN, MPanel.DEFAULT_CONTENT_MARGIN); 			else
				bottomPanel.setMargin(0, MPanel.DEFAULT_CONTENT_MARGIN, MPanel.DEFAULT_CONTENT_MARGIN, MPanel.DEFAULT_CONTENT_MARGIN);
			bottomPanel.setOpaque(false);
			if (flags.isSet(SIDE_BUTTONS)) {
				bottomPanel.addNorth(buttonsPanel);
				bottomPanel.addSouth(okCancelPanel);
			}
			else {
				bottomPanel.addWest(buttonsPanel);
				bottomPanel.addEast(okCancelPanel);
			}
			add(
				bottomPanel,
				flags.isSet(SIDE_BUTTONS)
				? BorderLayout.LINE_END 				: BorderLayout.PAGE_END 			);
						if (flags.isSet(OK_BUTTON))
				setDefault(okButton);
		}
		installComponents();
		titlePanel = MPanel.createBorderPanel();
		titlePanel.setOpaque(false);
		add(titlePanel, BorderLayout.PAGE_START); 
		if (flags.isClear(FORCE_STANDARD_BORDER) && !UI.isRetro()) {
			customBorder = true;
			if (UI.isRetro()) {
				getRootPane().setWindowDecorationStyle(JRootPane.PLAIN_DIALOG);
			}
			else {
				UI.setStyle("border-style: outset", getRootPane());
				if (UI.isSeaGlass())
