    public void setLocation(Location newLocation) {
        Colony oldColony = this.getColony();
        Location oldLocation = location;
        if (location != null) {
            if (location instanceof ColonyTile){
                                                ((ColonyTile)location).getWorkTile().setOwner(null);
            }
            location.remove(this);
        }
        location = newLocation;
        if (newLocation != null) {
            newLocation.add(this);
        }
                if (oldLocation instanceof WorkLocation) {
            if (!(newLocation instanceof WorkLocation)) {
                getOwner().modifyScore(-getType().getScoreValue());
                if (oldColony != null) {
                                        int newPopulation = oldColony.getUnitCount();
                    oldColony.updatePopulation(-1);
                    if (getState()!=UnitState.ACTIVE) {
                        logger.warning("Removing unit "+getId()+" with state=="+getState()+" (should not be IN_COLONY) from WorkLocation in "+oldLocation.getColony().getName()+". Fixing: ");
                        setState(UnitState.ACTIVE);
                    }
                }
            }
        } else if (newLocation instanceof WorkLocation) {
            getOwner().modifyScore(getType().getScoreValue());
            int oldPopulation = newLocation.getColony().getUnitCount();
            newLocation.getColony().updatePopulation(1);
            if (getState()!=UnitState.IN_COLONY) {
                logger.warning("Adding unit "+getId()+" with state=="+getState()+" (should be IN_COLONY) to WorkLocation in "+newLocation.getColony().getName()+". Fixing: ");
                setState(UnitState.IN_COLONY);
            }
        }
                if (!Utils.equals(oldColony, getColony())){
            setTurnsOfTraining(0);
        }
        if (student != null &&
            !(newLocation instanceof Building &&
              ((Building) newLocation).getType().hasAbility("model.ability.teach"))) {
                        student.setTeacher(null);
            student = null;
        }
        if (newLocation instanceof WorkLocation) {
            removeAllEquipment(false);
        } else if (teacher != null) {
            teacher.setStudent(null);
            teacher = null;
        }
                        if (getGame().getMap() != null && location != null && location instanceof Tile && !isNaval()) {
            contactAdjacent(getTile());
        }
        if (!getOwner().isIndian()) {
            getOwner().setExplored(this);
        }
    }
    public void contactAdjacent(Tile tile) {
        Iterator<Position> tileIterator = getGame().getMap().getAdjacentIterator(tile.getPosition());
        Player thisUnitOwner = getOwner();
                if(thisUnitOwner == null){
            throw new IllegalStateException("This unit has no owner");
        }
        while (tileIterator.hasNext()) {
            Tile t = getGame().getMap().getTile(tileIterator.next());
                        if (t == null || !t.isLand()) {
                continue;
            }
            Settlement settlement = t.getSettlement();
            Unit unitOnTile = t.getFirstUnit();
                        if(settlement == null && unitOnTile == null){
                continue;
            }
                        Player otherPlayer = null;
            if (settlement != null) {
                otherPlayer = settlement.getOwner();
            } else {
                otherPlayer = unitOnTile.getOwner(); 
            }
                        if(otherPlayer == null){
                throw new IllegalStateException("Cannot determine the other player.");    
            }
                        if(otherPlayer == thisUnitOwner){
                continue;
            }
                        if (!otherPlayer.hasContacted(thisUnitOwner)) {
                otherPlayer.setContacted(thisUnitOwner, true);
                thisUnitOwner.setContacted(otherPlayer, true);
            }
                                                            IndianSettlement indianSettlement = null;
            if (settlement != null){
                if(settlement instanceof IndianSettlement ){
                    indianSettlement = (IndianSettlement) settlement;
                }
            } else {
                                indianSettlement = unitOnTile.getIndianSettlement();
            }   
            if(indianSettlement != null && indianSettlement.getAlarm(thisUnitOwner) == null){
                indianSettlement.setAlarm(thisUnitOwner, otherPlayer.getTension(thisUnitOwner));
            }
        }
    }
    public void setIndianSettlement(IndianSettlement indianSettlement) {
        if (this.indianSettlement != null) {
            this.indianSettlement.removeOwnedUnit(this);
        }
        this.indianSettlement = indianSettlement;
        if (indianSettlement != null) {
            indianSettlement.addOwnedUnit(this);
        }
    }
