	public synchronized static MIcon getIcon(final String name, final int aSize) {
		if (name == null)
			return null;
		final IconCache iconCache = IconCache.getInstance();
				int size = (aSize == -1) ? aSize : TK.range(aSize, MIcon.DEFAULT_SIZE, MIcon.MIN_SIZE, MIcon.MAX_SIZE);
				String cachePath = name + "-" + size;
		MIcon icon = iconCache.get(cachePath);
		if (icon == null) {
			MIcon originalIcon = iconCache.get(name);
			int iconWidth;
			if (originalIcon == null) {
								if (userIconLoaders != null) {
					for (IconLoader iconLoader : userIconLoaders) {
						originalIcon = iconLoader.load(name, -1);
						if (originalIcon != null)
							break; 					}
				}
								if (originalIcon == null)
					originalIcon = defaultIconLoader.load(name, -1);
				if (originalIcon == null) {
					MLogger.error("core", "Could not load icon: \"%s\"", name);
					return null;
				}
				iconWidth = originalIcon.getIconWidth();
				if (iconWidth < 1) {
					MLogger.error("core", "Could not load icon: \"%s\"", name);
					return null;
				}
								iconCache.put(name, originalIcon);
			}
			else {
				iconWidth = originalIcon.getIconWidth();
							}
						if ((size != -1) && (size != iconWidth))
				icon = originalIcon.scale(size);
			else
				icon = originalIcon;
									iconCache.put(cachePath, icon);
		}
		return icon;
	}
