	}
	public static BufferedImage createCompatibleImage(final int w, final int h, final boolean alpha) {
		GraphicsEnvironment environment = GraphicsEnvironment.getLocalGraphicsEnvironment();
		GraphicsDevice device = environment.getDefaultScreenDevice();
		GraphicsConfiguration configuration = device.getDefaultConfiguration();
		if (alpha)
			return configuration.createCompatibleImage(w, h, Transparency.TRANSLUCENT);
		return configuration.createCompatibleImage(w, h);
	}
	public static Color deriveAlpha(final Color color, final int alpha) {
		return new Color(color.getRed(), color.getGreen(), color.getBlue(), alpha);
	}
	public static Image scaleImage(final Image image, final int w, final int h, final int hints, final boolean wait) {
		int currentWidth = image.getWidth(null);
		int currentHeight = image.getHeight(null);
		if ((currentWidth == w) && (currentHeight == h))
			return image;
		if (wait)
			return waitForImage(image.getScaledInstance(w, h, hints));
		return image.getScaledInstance(w, h, hints);
	}
	public static Object setAntialiasing(final Graphics2D g, final boolean value) {
		Key key = RenderingHints.KEY_ANTIALIASING;
		Object oldRenderingHint = g.getRenderingHint(key);
		g.setRenderingHint(key, value ? RenderingHints.VALUE_ANTIALIAS_ON : RenderingHints.VALUE_ANTIALIAS_OFF);
		return oldRenderingHint;
	}
