		Key key = RenderingHints.KEY_TEXT_ANTIALIASING;
		Object oldRenderingHint = g.getRenderingHint(key);
		g.setRenderingHint(key, value ? RenderingHints.VALUE_TEXT_ANTIALIAS_ON : RenderingHints.VALUE_TEXT_ANTIALIAS_OFF);
		return oldRenderingHint;
	}
	public static void setWaitCursor(final Component component, final boolean yes) {
		component.setCursor(Cursor.getPredefinedCursor(yes ? Cursor.WAIT_CURSOR : Cursor.DEFAULT_CURSOR));
	}
	public static void setWaitCursor(final boolean yes) {
		setWaitCursor(MFrame.getGlobal(), yes);
	}
	public static void strikeThrough(final Component component, final Graphics graphics, final String text) {
		strikeThrough(component, graphics, deriveAlpha(Color.RED, 127), text);
	}
	public static void strikeThrough(final Component component, final Graphics graphics, final Color color, final String text) {
		if (TK.isEmpty(text))
			return;
		Graphics2D g = (Graphics2D)graphics;
		g.setColor(color);
		int y1 = component.getHeight() / 2;
		FontMetrics metrics = g.getFontMetrics(component.getFont());
		int x2 = Math.min(component.getWidth() - 5, metrics.stringWidth(text));
		g.drawLine(5, y1, x2, y1);
	}
	public static BufferedImage toBufferedImage(final Icon source) {
		BufferedImage result = createCompatibleImage(source.getIconWidth(), source.getIconHeight(), true);
		Graphics2D g = result.createGraphics();
		source.paintIcon(null, g, 0, 0);
		g.dispose();
		return result;
	}
	public static BufferedImage toBufferedImage(final Image source) {
				BufferedImage result = createCompatibleImage(source.getWidth(null), source.getHeight(null), true);
		Graphics2D g = result.createGraphics();
		g.drawImage(source, 0, 0, null, null);
		g.dispose();
		return result;
	}
	public static Image waitForImage(final Image image) {
		MediaTracker mediaTracker = new MediaTracker(DummyComponent.getSharedInstance());
		mediaTracker.addImage(image, 0);
		try {
			mediaTracker.waitForID(0);
		}
		catch (InterruptedException exception) {
			MLogger.exception(exception);
		}
		return image;
	}
	@Uninstantiable private UI() { }
	public static interface EventsControl {
		public void setEventsEnabled(final boolean value);
	}
}"

