	public static Color getColor(final Color color, final float factor) {
		if (factor == 0.0f)
			return color;
		int R = color.getRed();
		int G = color.getGreen();
		int B = color.getBlue();
		R = Math.min(Math.max((int)(R * factor), 0), 255);
		G = Math.min(Math.max((int)(G * factor), 0), 255);
		B = Math.min(Math.max((int)(B * factor), 0), 255);
		return new Color(R, G, B);
	}
