		}
		public BufferedFileOutput(final String path) throws FileNotFoundException {
			super(new FileOutputStream(path));
		}
		public BufferedFileOutput(final String path, final boolean append) throws FileNotFoundException {
			super(new FileOutputStream(path, append));
		}
	}
	public static final class FileExtensionFilter implements FileFilter {
		private String[] extensions;
		public FileExtensionFilter(final String... extensions) {
			this.extensions = TK.copyOf(extensions);
		}
		public boolean accept(final File file) {
			if (!file.isFile())
				return false;
			String ext = FS.getExtension(file);
			for (String i : extensions) {
				if (i.equals(ext))
					return true;
			}
			return false;
		}
	}
	public static final class FileNameRegExpFilter implements FileFilter {
		private RegExp regExp;
		public FileNameRegExpFilter(final String pattern) {
			regExp = new RegExp(pattern);
		}
		public boolean accept(final File file) {
			return file.isFile() && regExp.matches(file.getName());
		}
	}
	public static final class Info extends Item<File> {
		private String device;
		private String type;
		public Info(final File mountPoint) {
			this(mountPoint, null, null);
		}
		public Info(final File mountPoint, final String device, final String type) {
			super(TK.checkNull(mountPoint, "mountPoint"));
			this.device = device;
			this.type = type;
			String s = mountPoint.getPath();
			if (type != null)
				s += (" (" + type + ")");
			setText(s);
		}
		public String getDevice() { return device; }
		public String getType() { return type; }
	}
	public static interface ProgressListener {
		public boolean updateProgress(final long complete, final long length);
	}
	public static final class TextReader extends BufferedReader {
		private FileInputStream fileInputStream;
		public TextReader(final InputStream input, final String encoding) throws UnsupportedEncodingException {
			super(new InputStreamReader(input, encoding));
			if (input instanceof FileInputStream)
				fileInputStream = (FileInputStream)input;
		}
		@Override
		public void close() throws IOException {
			super.close();
			FS.close(fileInputStream);
		}
	}
	public static final class TextWriter extends PrintWriter {
		private FileOutputStream fileOutputStream;
		public TextWriter(final OutputStream output, final String encoding) throws UnsupportedEncodingException {
			super(new BufferedWriter(new OutputStreamWriter(output, encoding)));
			if (output instanceof FileOutputStream)
				fileOutputStream = (FileOutputStream)output;
		}
		@Override
		public void close() {
			super.close();
			FS.close(fileOutputStream);
		}
	}
	private static final class DirectoryFilter implements FileFilter {
		public boolean accept(final File file) {
			return file.isDirectory();
		}
	}
	private static final class RegularFileFilter implements FileFilter {
		public boolean accept(final File file) {
			return file.isFile();
		}
	}
