	public static List<Info> listFileSystems(final int flags) {
		Flags f = new Flags(flags);
		List<Info> result = new ArrayList<Info>();
		if (OS.isWindows()) {
			for (File i : File.listRoots())
				result.add(new Info(i));
		}
		else {
			try {
				String tabFile = f.isSet(LIST_ALL_FILE_SYSTEMS) ? "/etc/fstab" : "/etc/mtab";
				boolean excludeSpecial = f.isSet(LIST_EXCLUDE_SPECIAL);
				Set<String> excludeSet = null;
				if (excludeSpecial) {
					excludeSet = new HashSet<String>();
					excludeSet.add("binfmt_misc");
					excludeSet.add("devpts");
					excludeSet.add("proc");
					excludeSet.add("securityfs");
					excludeSet.add("sysfs");
					excludeSet.add("tmpfs");
				}
				String table = read(tabFile, "ISO-8859-1");
				if (!TK.isEmpty(table)) {
					for (String entry : TK.fastSplit(table, '\n')) {
						entry = entry.trim();
						if (entry.isEmpty() || (entry.charAt(0) == '#'))
							continue; 						
						int index = 0;
						Scanner s = new Scanner(entry);
						String device = null;
						String dir = null;
						String type = null;
						while (s.hasNext()) {
							switch (index) {
								case 0:
									device = s.next();
									break;
								case 1:
									dir = s.next();
									break;
								case 2:
									type = s.next();
									break;
							}
							index++;
							if (index == 3) {
								if (excludeSpecial && (type != null) && excludeSet.contains(type))
									break; 
								result.add(new Info(new File(dir), device, type));
								break; 							}
						}
					}
				}
			}
			catch (IOException exception) {
				MLogger.exception(exception);
				for (File i : File.listRoots())
					result.add(new Info(i));
			}
		}
		if (MLogger.isDebug("fs")) {
			for (Info i : result) {
				MLogger.debug("fs", i.toString());
				MLogger.debug("fs", "mountPoint=\"%s\",device=\"%s\",type=\"%s\"", i.getValue(), i.getDevice(), i.getType());
			}
		}
		return result;
	}
