"package org.makagiga.todo;
import static org.makagiga.commons.UI._;
import java.awt.Color;
import java.io.Serializable;
import java.util.EnumSet;
import org.makagiga.commons.ColorProperty;
import org.makagiga.commons.HTMLBuilder;
import org.makagiga.commons.Important;
import org.makagiga.commons.MDate;
import org.makagiga.commons.MObject;
import org.makagiga.commons.Obsolete;
import org.makagiga.commons.Searchable;
import org.makagiga.commons.TK;
import org.makagiga.commons.WTFError;
public class Task
implements
	Cloneable,
	Searchable<String>,
	Serializable
{
	private int complete;
	private MDate completeDateTime = MDate.invalid();
	private MDate dateTime = MDate.invalid();
	private MDate startDateTime = MDate.invalid();
	private Priority priority = Priority.DEFAULT;
	private String category = "";
	private String summary = "";
	public Task() { }
	@Deprecated
	public Task(final Task other) {
		this(
			other.summary,
			other.priority,
			other.complete,
			other.dateTime,
			other.startDateTime,
			other.completeDateTime
		);
		setCategory(other.category);
	}
	@Deprecated
	public Task(
		final String summary,
		final Priority priority,
		final int complete,
		final MDate dateTime,
		final MDate startDateTime,
		final MDate completeDateTime
	) {
		this(
			summary,
			priority,
			complete,
			dateTime.getTime(),
			startDateTime.getTime(),
			completeDateTime.getTime()
		);
	}
	public Task(
		final String summary,
		final Priority priority,
		final int complete,
		final long dateTime,
		final long startDateTime,
		final long completeDateTime
	) {
		this.priority = priority;
		this.complete = TK.range(complete, 0, 0, 100);
		setSummary(summary);
		setDateTime(dateTime);
		setStartDateTime(startDateTime);
		setCompleteDateTime(completeDateTime);
	}
	@Override
	public boolean equals(final Object other) {
		switch (MObject.maybeEquals(this, other)) {
			case YES:
				return true;
			case NO:
				return false;
			default: 				Task task = (Task)other;
				return (
					(this.complete == task.complete) &&
					(this.priority == task.priority) &&
					this.category.equals(task.category) &&
					this.summary.equals(task.summary) &&
					this.dateTime.equals(task.dateTime) &&
					this.startDateTime.equals(task.startDateTime) &&
					this.completeDateTime.equals(task.completeDateTime)
				);
		}
	}
	public String formatComplete() {
		return formatComplete(complete);
	}
	public static String formatComplete(final int value) {
		return value + "%";
	}
	public String formatDate() {
		return formatDate(getDateTimeState());
	}
	public String formatDate(final TaskState state) {
		return formatDate(dateTime, state);
	}
	public static String formatDate(final MDate value, final TaskState state) {
		if (!value.isValid())
			return "";
		if (state == TaskState.TODAY)
			return value.fancyFormat(MDate.MEDIUM, true);
		return value.formatDateTime(MDate.MEDIUM, MDate.SHORT);
	}
	public String getCategory() { return category; }
	public void setCategory(final String value) {
		category = (value == null) ? "" : value;
	}
	public MDate getCompleteDateTime() { return completeDateTime; }
	public void setCompleteDateTime(final long value) {
		completeDateTime.setTime(value);
	}
	public void setCompleteDateTime(final MDate value) {
		completeDateTime.setTime(value);
	}
	public MDate getDateTime() { return dateTime; }
	public void setDateTime(final long value) {
		dateTime.setTime(value);
	}
	public void setDateTime(final MDate value) {
		dateTime.setTime(value);
	}
	public TaskState getDateTimeState() {
		return getTaskState(dateTime, MDate.now());
	}
	public static TaskState getDateTimeState(final MDate dueDateTime) {
		return getTaskState(dueDateTime, MDate.now());
	}
	public TaskState getDateTimeStateNow(final MDate now) {
		return getTaskState(dateTime, now);
	}
	public MDate getStartDateTime() { return startDateTime; }
	public void setStartDateTime(final long value) {
		startDateTime.setTime(value);
	}
	public void setStartDateTime(final MDate value) {
		startDateTime.setTime(value);
	}
	public int getComplete() { return complete; }
	public void setComplete(final int value) { complete = value; }
	public Priority getPriority() { return priority; }
	public void setPriority(final Priority value) { priority = value; }
	public String getSummary() { return summary; }
	public void setSummary(final String value) {
		summary = (value == null) ? "" : value;
	}
	public String getToolTipTable(final boolean includeSummary) {
		HTMLBuilder html = new HTMLBuilder();
		html.beginTag("table", "cellpadding", 2, "cellspacing", 1);
		String code;
		if (includeSummary) {
			html.beginTag("tr");
			if (summary.isEmpty())
				code = "<i>" + HTMLBuilder.escape(_("No Summary")) + "</i>";
			else
				code = HTMLBuilder.escape(TK.centerSqueeze(summary, 100));
			html.doubleTag("td", code, "colspan", 2);
			html.endTag("tr");
		}
		html.beginTag("tr");
		Priority p = getPriority();
		Color priorityColor = p.getColor();
		String priorityStyle = String.format(
			"background-color: %s; color: %s; text-align: center",
			(priorityColor == null) ? "white" : ColorProperty.toString(priorityColor),
			ColorProperty.toString(Color.BLACK)
		);
		html.doubleTag("td", HTMLBuilder.escape(p.toString()), "style", priorityStyle);
		html.doubleTag("td", HTMLBuilder.escape(formatComplete()), "style", "text-align: center");
		html.endTag("tr");
		long time = dateTime.getTime();
		if (time != 0) {
			TaskState state = getDateTimeState();
			String dateStyle =
				"background-color: " + ColorProperty.toString(state.getColorForPaint()) +
				"; color: " + ColorProperty.toString(Color.BLACK);
			html.beginTag("tr", "style", dateStyle);
			html.doubleTag("td", HTMLBuilder.escape(formatDate()));
			html.doubleTag("td", HTMLBuilder.escape(state.toString()), "style", "text-align: center");
			html.endTag("tr");
			if ((state == TaskState.LATER) || (state == TaskState.TODAY)) {
				long msTo = time - MDate.currentTime();
				if (msTo > 0) {
					long seconds = msTo / 1000;
					long hours = seconds / 3600;
					long minutes = (seconds / 60) % 60;
					long days = hours / 24;
					String timeTo =
						_("Time To: {0} day(s), {1} hour(s), {2} minute(s)",
							days,
							hours - days * 24,
							minutes
						);
					html.beginTag("tr");
					html.doubleTag("td", HTMLBuilder.escape(timeTo), "colspan", 2);
					html.endTag("tr");
				}
			}
		}
		if (!category.isEmpty()) {
			html.beginTag("tr");
			html.doubleTag("td", _("Category: {0}", HTMLBuilder.escape(category), "colspan", 2));
			html.endTag("tr");
		}
		html.endTag("table");
		return html.toString();
	}
	public String getToolTipText() {
		HTMLBuilder html = new HTMLBuilder();
		html.beginHTML();
		html.beginDoc();
		html.setIndentLevel(0);
		html.appendLine(getToolTipTable(true));
		html.endDoc();
		return html.toString();
	}
	@Override
	public int hashCode() {
		int result = MObject.hashCode(priority, category, summary, dateTime, startDateTime, completeDateTime);
		result = 31 * result + complete;
		return result;
	}
	public boolean isDone() {
		return (complete == 100);
	}
	@Deprecated
	public boolean matches(final String filter, final boolean caseSensitive) {
		if (TK.isEmpty(filter))
			return true;
		if (caseSensitive) {
			if (
				getSummary().contains(filter) ||
				getCategory().contains(filter)
			)
				return true;
		}
		else {
			if (
				TK.containsIgnoreCase(getSummary(), filter) ||
				TK.containsIgnoreCase(getCategory(), filter)
			)
				return true;
		}
		MDate date = getDateTime();
		MDate completeDate = getCompleteDateTime();
		MDate startDate = getStartDateTime();
		if (caseSensitive) {
			return (
				(date.isValid() && formatDate(date, TaskState.UNKNOWN).contains(filter)) ||
				(completeDate.isValid() && formatDate(completeDate, TaskState.UNKNOWN).contains(filter)) ||
				(startDate.isValid() && formatDate(startDate, TaskState.UNKNOWN).contains(filter))
			);
		}
		else {
			return (
				(date.isValid() && TK.containsIgnoreCase(formatDate(date, TaskState.UNKNOWN), filter)) ||
				(completeDate.isValid() && TK.containsIgnoreCase(formatDate(completeDate, TaskState.UNKNOWN), filter)) ||
				(startDate.isValid() && TK.containsIgnoreCase(formatDate(startDate, TaskState.UNKNOWN), filter))
			);
		}
	}
	@Obsolete 	public static long parseDateTime(final String value) {
		if (TK.isEmpty(value))
			return 0;
				if (value.contains("T")) {
			MDate result = MDate.parseRFC3339(value);
			return (result == null) ? 0 : result.getTime();
		}
				try {
			return Long.parseLong(value);
		}
		catch (NumberFormatException exception) {
			return 0;
		}
	}
	@Important
	@Override
	public String toString() {
		return toString(-1);
	}
	public String toString(final int maxSummaryLength) {
		String format =
									(maxSummaryLength < 1)
			? "%s\t%s\t%s%s%s\n"
			: ("%-" + maxSummaryLength + "s\t%s\t%s%s%s\n");
		return String.format(
			format,
			summary,
			priority,
			formatComplete(),
			dateTime.isValid() ? ('\t' + formatDate(TaskState.UNKNOWN)) : "",
			!category.isEmpty() ? ('\t' + category) : ""
		);
	}
	@Obsolete 	@Override
	public Object clone() {
		try {
			Task task = (Task)super.clone();
						task.completeDateTime = (MDate)this.completeDateTime.clone();
			task.dateTime = (MDate)this.dateTime.clone();
			task.startDateTime = (MDate)this.startDateTime.clone();
			return task;
		}
		catch (CloneNotSupportedException exception) {
			throw new WTFError(exception);
		}
	}
	@Override
	public boolean matches(final String filter, final EnumSet<Searchable.Matches> options) {
		return matches(filter, options.contains(Searchable.Matches.CASE_SENSITIVE));
	}
	private static TaskState getTaskState(final MDate date, final MDate now) {
		switch (date.compareDateTime(now, true)) {
			case BEFORE:
				return TaskState.OVERDUE;
			case TODAY:
				return TaskState.TODAY;
			case AFTER:
				return TaskState.LATER;
			default: 				return TaskState.UNKNOWN;
		}
	}
}"

