	protected MApplication() {
		_instance = this;
		MLogger.info("core", getTitle());
		EventQueue.invokeLater(new Runnable() {
			public void run() {
				if (Args.check())
					return;
				ToolTipManager toolTipManager = ToolTipManager.sharedInstance();
				toolTipManager.setDismissDelay(8000); 
				if (!MSplashScreen.isAvailable() && !Args.isSet("no-splash"))
					MSplashScreen.setImage(new MIcon(new File("splash.png")).getImage());
				startup();
				initialized = true;
				MSplashScreen.close();
								MMainWindow mainWindow = getMainWindow();
				if (mainWindow != null) {
					if (MLogger.isDeveloper()) {
						JMenuBar menuBar = mainWindow.getJMenuBar();
						if (menuBar != null)
							menuBar.add(MLogger.createMemoryPanel());
					}
					MStatusBar statusBar = getStatusBar();
					if (statusBar != null) {
						statusBar.initPlatform(mainWindow);
					}
				}
								if (isSafeMode()) {
					Config config = Config.getDefault();
					config.write("safeMode", false);
					config.sync();
				}
				if (!OS.isSupported()) {
					MNotification.Message m = new MNotification.Message(
						_("Information"),
						_("This operating system ({0}) is not fully supported", OS.getName()),
						"ui/info"
					);
					m.setTimeout(-1);
					MNotification.showMessage(m);
				}
				installShutDownHooks();
			}
		} );
	}
	protected boolean confirmQuit() {
		if (!confirmExit.get())
			return true;
		Component oldGlassPane = null;
		JComponent grayBackground = null;
		MMainWindow mw = getMainWindow();
		if (mw != null) {
			if (mw.isLocked())
				return true;
			oldGlassPane = mw.getGlassPane();
			mw.clearGlassPane();
			mw.restore();
			grayBackground = new MComponent() {
				@Override
				public void paintComponent(final Graphics graphics) {
					Graphics2D g = (Graphics2D)graphics;
					g.setColor(UI.TRANSPARENT_BLACK);
					g.fillRect(0, 0, getWidth(), getHeight());
				}
			};
			oldGlassPane.setVisible(false);
			mw.setGlassPane(grayBackground);
			grayBackground.setVisible(true);
		}
		boolean result = MMessage.simpleConfirm(mw, MActionInfo.QUIT);
		if (mw != null) {
			grayBackground.setVisible(false);
			mw.setGlassPane(oldGlassPane);
		}
		return result;
	}
